import portfolioImg01 from "../images/SnapIntro.png";
import portfolioImg02 from "../images/Product.png";
import portfolioImg03 from "../images/LoopStudios.png";
import portfolioImg04 from "../images/TodoAoo.png";
import portfolioImg05 from "../images/quizgame.png";

const portfolios = [
  {
    id: "01",
    imgUrl: portfolioImg01,
    category: "Web Design",
    title: "Intro section",
    description:
      "This challenge will test your ability to create dropdown navigation menus, a common pattern on larger sites. It will also provide some nice basic layout challenges.",
    technologies: ["HTML", "Css", "Javascript", ],
    siteUrl: "#",
  },
  {
    id: "02",
    imgUrl: portfolioImg02,
    category: "Web Design",
    title: "E-commerce product page",
    description:
      "In this challenge, you'll build a beautiful product page. We'll be putting your JS skills to the test with a lightbox product gallery and cart functionality!",
    technologies: ["Html", "Css", "Javascript", ],
    siteUrl: "#",
  },
  {
    id: "03",
    imgUrl: portfolioImg03,
    category: "Web Design",
    title: "Loopstudios landing page",
    description:
      "This challenge is perfect if you're looking to test your CSS Grid chops. Even without Grid, this project will be a fun one to help you practice your layout skills!",
    technologies: ["Html", "Css", "Javascript", ],
    siteUrl: "#",
  },
  {
    id: "04",
    imgUrl: portfolioImg04,
    category: "Apps",
    title: "TODO",
    description:
      "Feeling overwhelmed by tasks? Conquer your day with Get It Done, a simple to-do app that helps you add, check off, and manage tasks - all for ultimate organization and increased productivity. Start focusing on what matters, not endless lists, and achieve your goals with ease. Let Get It Done be your key to a less stressful, more accomplished you.",
    technologies: ["Html", "Css", "javascript", "MongoDB"],
    siteUrl: "#",
  },
  {
    id: "05",
    imgUrl: portfolioImg05,
    category: "Apps",
    title: "Countries Quiz Game",
    description:
      "Calling all globetrotters! Test your geography IQ with the Ultimate Countries Quiz! Choose your difficulty, answer flag, capital, and landmark questions, and rack up points. But beware, wrong answers cost lives! Can you conquer the globe and top the leaderboard? It's time to explore and see who reigns supreme!",
    technologies: ["ReactJS", "Bootstrap", "Laravel","API","Redux"],
    siteUrl: "#",
  },
  // {
